import java.util.Scanner;public class FeeNew {	public static void main (String[] args){		double fee;		double discount_percentage;		double discount_amount;		double final_amount;				Scanner input = new Scanner(System.in);		System.out.print("Enter fee : ");		fee = input.nextDouble();		System.out.print("Enter university discount (%): ");		discount_percentage = input.nextDouble();		discount_amount = (discount_percentage/100) * fee;		final_amount = fee - discount_amount;				System.out.println("Original University fee : $" +fee);		System.out.println("Discount provided by University : " +discount_percentage+ "%");		System.out.println("Final amount to be paid : $" +final_amount);				input.close();	}}